import {Row,Col,ListGroup,Card,Button} from "react-bootstrap"
import Rating from '../components/Rating'
const ProductScreen = () => {
    let { id } = useParams();
   const Product=products.find((p)=>(p._id==id))
    
    return (

        <div>
            <h1>{Product.name}</h1>
        </div>
    )
}

export default ProductScreen
