import React, { useState, useEffect } from "react";
import FormContainer from "../components/formContainer";
import {Form ,Button } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { listProductDetails} from "../Actions/productAction";
import Message from "../components/message";
import Loader from "../components/loader";
import {useNavigate } from "react-router";
import { useParams } from "react-router";
import { USER_UPDATE_RESET } from "../Constants/userConstants";
 
const ProductEditScreen = () => {
 const {id:productId}=useParams()

  const [name, setName] = useState('');
  const [price, setPrice] = useState(0);
  const [image, setImage] = useState('');
  const [brand, setBrand] = useState('');
  const [category, setCategory] = useState('');
  const [description, setDescription] = useState('');
  const [countInStock, setCountInStock] = useState(0);
  
  
   const navigate=useNavigate()
   const dispatch=useDispatch()

  const productDetails=useSelector(state=>state.productDetails)
  const {loading,Product,error}=productDetails

  const userUpdate=useSelector(state=>state.userUpdate)
  const {loading:loadingUpdate,success:successUpdate,error:errorUpdate}=userUpdate

  useEffect(()=>{
      
        if(!Product.name||Product._id!==productId){
            dispatch(listProductDetails(productId))
        }else{
            setName(Product.name)
            setImage(Product.image)
            setBrand(Product.brand)
            setCountInStock(Product.countInStock)
            setDescription(Product.description)
            setPrice(Product.price)
            setCategory(Product.category)
            
        }
     
  },[dispatch,Product,productId,navigate])

  
  const submitHandler=(e)=>{ 
    e.preventDefault() 
   console.log(Product.name)
         
    }
    
  return (
    <>
    <Link to={`/admin/productlist`} className='btn btn-light mv-3'>Go Back</Link>
    
   <FormContainer>
        <h1>Edit Products </h1>
        {/* {loadingUpdate&&<Loader/>}
        {errorUpdate&&<Message variant='danger'>{errorUpdate}</Message>} */}
        
        {loading ?<Loader/>:error?<Message variant='danger'>{error}</Message>:(
            <Form onSubmit={submitHandler}>
      
            <Form.Group  controlId="name">
                <Form.Label>Name</Form.Label>
                <Form.Control type="text" placeholder="Enter name" value={name} onChange={(e)=>(setName(e.target.value))}/>
               
              </Form.Group>

              <Form.Group  controlId="image">
                <Form.Label>Image</Form.Label>
                <Form.Control type="text" placeholder="Enter Image" value={image} onChange={(e)=>(setImage(e.target.value))}/>
               
              </Form.Group>
      
              <Form.Group  controlId="price">
                <Form.Label>Price address</Form.Label>
                <Form.Control type="number" placeholder="Enter price" value={price} onChange={(e)=>(setPrice(e.target.value))}/>
               
              </Form.Group>

              
              <Form.Group  controlId="brand">
                <Form.Label>Brand</Form.Label>
                <Form.Control type="text" placeholder="Enter brand" value={brand} onChange={(e)=>(setBrand(e.target.value))}/>
               
              </Form.Group>

              <Form.Group  controlId="description">
                <Form.Label>Description</Form.Label>
                <Form.Control type="text" placeholder="Enter description" value={description} onChange={(e)=>(setDescription(e.target.value))}/>
               
              </Form.Group>
              <Form.Group  controlId="countInStock">
                <Form.Label>CountInStock</Form.Label>
                <Form.Control type="number" placeholder="Enter countInStock" value={countInStock} onChange={(e)=>(setCountInStock(e.target.value))}/>
               
              </Form.Group>
              <Form.Group  controlId="category">
                <Form.Label>Category</Form.Label>
                <Form.Control type="text" placeholder="Enter category" value={category} onChange={(e)=>(setCategory(e.target.value))}/>
               
              </Form.Group>
                           
              <Button variant="dark" type="submit">
                UPDATE  
              </Button>
            </Form>
        )} 
    </FormContainer>
    
   </>
   
  )
}

export default ProductEditScreen;
