import React from 'react'
// import products from '../products'
import { useParams ,Link,useHistory} from 'react-router-dom'
import {Row,Col,ListGroup,Card,Button,Image, ListGroupItem, Form} from "react-bootstrap"
import Rating from '../components/Rating'
import { useState,useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { listProductDetails } from '../Actions/productAction'
import Loader from '../components/loader'
import Message from '../components/message'
import axios from 'axios'


const ProductScreen = () => {

    const [qty,setqty]=useState(0)
    
//    const Product=products.find((p)=>(p._id==id))

    const dispatch=useDispatch()
    

   const productDetails=useSelector((state)=>state.productDetails)
    // console.log(productDetails.Product);
    
 const {loading,error,Product}=productDetails
    const { id } = useParams();
//    const [Product, setProduct] = useState({})
   useEffect(()=>{
        //    const fetchProducts =async()=>{
        //        const {data}=await axios.get(`/Api/products/${id}`)
        //        setProduct(data)
        //    }
        //    fetchProducts()
        
        
          dispatch(listProductDetails(id))
   },[dispatch,id]);
// const Product={}
const history= useHistory()
    const AddtoCartHandler=()=>{
        history.push(`/cart/${id}/?qty=${qty}`)
    }
    return (

        <> <h1>{Product.name}</h1>  

{loading?<Loader/>:error?<Message variant='danger'>{error}</Message>:(
<Row>
<Col md={6}>
    <Image src={Product.image} alt={Product.name} fluid/>
</Col>
<Col md={3} variant="flush">   
 <ListGroup>
        <ListGroup.Item>
            <h3>{Product.name}</h3>
        </ListGroup.Item>
        <ListGroup.Item>
            <Rating Value={Product.rating} text={`${Product.numReviews} reviews`}/>
        </ListGroup.Item>
        <ListGroup.Item>
           Price:₹ {Product.price}
        </ListGroup.Item>
        <ListGroup.Item>
            {Product.description}
        </ListGroup.Item>
   </ListGroup>
   
</Col>
<Col md={3}>
      <Card>
          <ListGroup variant="flush">
              <ListGroup.Item>
              <Row>
              <Col>
              Price:
              </Col>
              <Col>
              <strong>₹ {Product.price}</strong>
              </Col>
              </Row>
              </ListGroup.Item>

              <ListGroup.Item>
                  <Row>
                      <Col>
                      Status :
                      </Col>
                      <Col>
                      {Product.countInStock>0?'In stock':'Out of Stock'}
                      </Col>
                  </Row>
              </ListGroup.Item>
              {Product.countInStock>0 && (
                     <ListGroup.Item>
                      <Row>
                         <Col>Quantity :</Col>
                          <Col>
                          <Form.Control as='select' value={qty} onChange={(e)=>setqty(e.target.value)}>
                           {[...Array(Product.countInStock).keys()].map(x=>(
                               <option key={x+1} value={x+1}>
                                 {x+1}
                               </option>
                           ))}
                          </Form.Control>
                          </Col>
                      </Row>
                     </ListGroup.Item>
                )}

               
              <ListGroup.Item>
                  <Button className="btn-block" type='button' onSubmit={AddtoCartHandler} disabled={Product.countInStock===0}>Add to cart</Button>
              </ListGroup.Item>
          </ListGroup>
      </Card>

</Col>
</Row>)
}
            
        </>
    )
}

export default ProductScreen
