import React, { useState, useEffect } from "react";
import Loader from "../components/loader";
import {
  Form,
  Button,
  Row,
  Card,
  Image,
  Col,
  ListGroup,
  ListGroupItem,
} from "react-bootstrap";
import { PayPalButton } from "react-paypal-button-v2";
import { useDispatch, useSelector } from "react-redux";
import { getOrderDetails, payOrder } from "../Actions/orderAction";
import { Link, useParams } from "react-router-dom";
import Message from "../components/message";
import axios from "axios";
import { ORDER_PAY_RESET } from "../Constants/orderConstants";

const OrderScreen =  () => {
  const razorpayment = async () => {


    const script = document.createElement("script");
    script.type = "text/javascript";
    script.async = true;
    script.src = `https://checkout.razorpay.com/v1/checkout.js`;
    // script.onload = () => {
    //   setSdkReady(true);
    // };
    document.body.appendChild(script);




    const razData = await axios.post("/Api/config/razorpay");
    console.log(razData);

    var options = {
      key: "rzp_test_X4PSgEssJGWsmS",
      //"amount": `${order.totalPrice}`,
      amount: "5000",
      currency: "INR",
      name: "Acme Corp",
      description: "Test Transaction",
      image: "https://example.com/your_logo",
      order_id: `${razData.data.id}`,
      // "callback_url": "https://eneqd3r9zrjok.x.pipedream.net/"
    };

    const razorPayment = new window.Razorpay(options);
    razorPayment.open()
  };

  

  const { id: orderId } = useParams();

  const orderDetails = useSelector((state) => state.orderDetails);
  const { error, loading, order } = orderDetails;

  const [sdkReady, setSdkReady] = useState(false);
  const orderPay = useSelector((state) => state.orderPay);
  const { loading: loadingPay, success: successPay } = orderPay;

  const dispatch = useDispatch();
  useEffect(() => {
    // const addPaypalScript = async () => {
    //   const { data: clientId } = await axios.get("/Api/config/paypal");
    //   const script = document.createElement("script");
    //   script.type = "text/javascript";
    //   script.async = true;
    //   script.src = `https://www.paypal.com/sdk/js?client-id=${clientId}`;
    //   script.onload = () => {
    //     setSdkReady(true);
    //   };
    //   document.body.appendChild(script);
    // };

    //if(order=== undefined)
    //{
    if (!order || successPay) {
      dispatch({ type: ORDER_PAY_RESET });
      dispatch(getOrderDetails(orderId));
    }
    //  else if (!order.isPaid) {
    //   if (!window.paypal) {
    //     addPaypalScript();
    //   } else {
    //     setSdkReady(true);
    //   }
    // }
    //}
  }, [dispatch, orderId, order, successPay]);

  const successPaymentHandler = (paymentResult) => {
    console.log(paymentResult);
    dispatch(payOrder(orderId,paymentResult));
  };

  console.log(order);

  return loading ? (
    <Loader />
  ) : error ? (
    <Message variant="danger">{error}</Message>
  ) : (
    <>
      {order !== undefined && (
        <>
          <h2>Order Id</h2>{" "}
          <u>
            <h5>{order._id}</h5>
          </u>
          {/*❤ error can occur*/}
          <Row>
            <Col md={8}>
              <ListGroup variant="flush">
                <ListGroup.Item>
                  <h2>Shipping Details</h2>
                  <p>
                    <strong>Name :</strong>
                    {order.user.name}
                  </p>
                  <p>
                    {" "}
                    <strong>Email :</strong>
                    <a href={`mailto:${order.user.email}`}>
                      {order.user.email}
                    </a>
                  </p>
                  <p>
                    <strong>Address: </strong>
                    {order.shippingAddress.address},{order.shippingAddress.city}{" "}
                    {order.shippingAddress.postalCode}{" "}
                    {order.shippingAddress.country}
                  </p>
                  {order.isDelivered ? (
                    <Message variant="success">
                      Delivered On {order.deliveredAt}
                    </Message>
                  ) : (
                    <Message variant="danger">Not Delivered</Message>
                  )}
                </ListGroup.Item>

                <ListGroup.Item>
                  <h2>Payment Method</h2>
                  <p>
                    <strong>METHOD:</strong>
                    {order.paymentMethod}
                  </p>
                  {order.isPaid ? (
                    <Message variant="success">Paid On {order.paidAt}</Message>
                  ) : (
                    <Message variant="danger">Not Paid</Message>
                  )}
                </ListGroup.Item>

                <ListGroupItem>
                  <h2>Order Items</h2>
                  {order.orderItems.length === 0 ? (
                    <Message>Order is Empty</Message>
                  ) : (
                    <ListGroupItem variant="flush">
                      {order.orderItems.map((item, index) => (
                        <ListGroupItem key={index}>
                          <Row>
                            <Col md={1}>
                              <Image
                                src={item.image}
                                alt={item.name}
                                fluid
                                rounded
                              ></Image>
                            </Col>
                            <Col>
                              <Link to={`/product/${item.product}`}>
                                {item.name}
                              </Link>
                            </Col>
                            <Col md={4}>
                              {item.qty} x ₹{item.price}= ₹{" "}
                              {item.qty * item.price}
                            </Col>
                          </Row>
                        </ListGroupItem>
                      ))}
                    </ListGroupItem>
                  )}
                </ListGroupItem>
              </ListGroup>
            </Col>

            <Col md={4}>
              <Card>
                <ListGroup variant="flush">
                  <ListGroupItem>
                    <h2>Order Summary</h2>
                  </ListGroupItem>
                  <ListGroupItem>
                    <Row>
                      <Col>Items</Col>
                      <Col>₹{order.itemsPrice}</Col>
                    </Row>
                  </ListGroupItem>
                  <ListGroupItem>
                    <Row>
                      <Col>Shipping</Col>
                      <Col>₹{order.shippingPrice}</Col>
                    </Row>
                  </ListGroupItem>
                  <ListGroupItem>
                    <Row>
                      <Col>Taxes</Col>
                      <Col>₹{order.taxPrice}</Col>
                    </Row>
                  </ListGroupItem>
                  <ListGroupItem>
                    <Row>
                      <Col>Total Price</Col>
                      <Col>₹{order.totalPrice}</Col>
                    </Row>
                  </ListGroupItem>

                  {!order.isPaid && (
                    <ListGroupItem>
                      {loadingPay && <Loader />}
                      {!sdkReady ? (
                        <Loader />
                      ) : (
                        <PayPalButton
                          amount={order.totalPrice}
                          onSuccess={successPaymentHandler}
                        />
                      )}
                    </ListGroupItem>
                  )}

                  <ListGroupItem>
                    <Button type='button' onClick={razorpayment}>Pay With Razorpay</Button>
                  </ListGroupItem>
                </ListGroup>
              </Card>
            </Col>
          </Row>
        </>
      )}
    </>
  );
};

export default OrderScreen;
